import { InterpretationError } from "./interpretationError";

export default class NameError extends InterpretationError {
  name;

  constructor(traceback: string) {
    super(traceback);
    this.name = NameError.getVariableFunctionName(traceback);
  }

  static getVariableFunctionName(traceback: string) {
    let padrao = /name \'([a-zA-Z_-]+)\'/;
    let consulta = traceback.match(padrao);

    if (consulta != null) {
      return consulta[1];
    }

    return null;
  }
}
